bloom.fs.glsl:
#version 300 es

#ifdef GL_FRAGMENT_PRECISION_HIGH
precision highp float;
#else
precision mediump float;
#endif

uniform sampler2D       _MainTex;//清晰图
uniform sampler2D       _BlurTex;//模糊高光的图
uniform highp vec4      _MainTex_TexelSize;

uniform highp float     _bloomIntensity;
// 强度 - 附加光（影响到该特效的光源）的全局光强
uniform highp vec4     _blurSpread;
// 模糊扩散 - Frag down sample 的偏移距离
uniform highp float     _bloomThreshold;
// 阈值 - 图像中亮度高于该阈值的区域将产生泛光效果

in highp vec2      xlv_TEXCOORD0;   // 每个片元的纹素坐标

#define INTENSITY       _bloomIntensity
#define THRESHOLD       _bloomThreshold
#define BLUR_SPREAD     vec2(_blurSpread.xy * _MainTex_TexelSize.xy)

#define tex(t, uv)      texture((t), (uv))

//texture2DEtC1Mark

float luminance(vec3 rgb) {
    const vec3 w = vec3(0.2125, 0.7154, 0.0721);
    return dot(rgb, w);
}
// Blur spread 扩撒
vec4 fragDownsample(sampler2D image, vec2 uv) {
    vec2 uv2[4];
    uv2[0] = uv + _MainTex_TexelSize.xy * vec2(1.5, 1.5);
    uv2[1] = uv + _MainTex_TexelSize.xy * vec2(-1.5, 1.5);
    uv2[2] = uv + _MainTex_TexelSize.xy * vec2(-1.5, -1.5);
    uv2[3] = uv + _MainTex_TexelSize.xy * vec2(1.5, -1.5);
    vec4 color;
    color += texture(image, uv2[0]);
    color += texture(image, uv2[1]);
    color += texture(image, uv2[2]);
    color += texture(image, uv2[3]);
    return max(color/4.0 - THRESHOLD, vec4(0.0)) * INTENSITY;
}
vec4 fastBlur(sampler2D image, vec2 uv, vec2 netFilterWidth) {

    vec4 blur = vec4(0.0);
    vec2 _offset[7];
    vec4 curve[7];
    curve[0] = vec4(0.0205, 0.0205, 0.0205, 0.0);
    curve[1] = vec4(0.0855, 0.0855, 0.0855, 0.0);
    curve[2] = vec4(0.232, 0.232, 0.232, 0.0);
    curve[3] = vec4(0.324, 0.324, 0.324, 1.0);
    curve[4] = vec4(0.232, 0.232, 0.232, 0.0);
    curve[5] = vec4(0.0855, 0.0855, 0.0855, 0.0);
    curve[6] = vec4(0.0205, 0.0205, 0.0205, 0.0);
    // horizontal or vertical blur, defined by netFilterWidth vector
    _offset[0] = uv + netFilterWidth * 3.0;
    _offset[1] = uv + netFilterWidth * 2.0;
    _offset[2] = uv + netFilterWidth;
    _offset[3] = uv;
    _offset[4] = uv - netFilterWidth;
    _offset[5] = uv - netFilterWidth * 2.0;
    _offset[6] = uv - netFilterWidth * 3.0;
    for(int l = 0; l < 7; l++) {
        blur += tex(image, _offset[l]) * curve[l];
    }

    return blur;
}

out vec4 color; 
void main () {
    if(BLUR_SPREAD.x == 0.0 && BLUR_SPREAD.y == 0.0) {    // 不泛光, 只过滤
        color = fragDownsample(_MainTex, xlv_TEXCOORD0);
    } else if(THRESHOLD == 1.0){    // 不过滤, 只泛光
        color = fastBlur(_MainTex, xlv_TEXCOORD0, BLUR_SPREAD);
    } else {    // Final Composition
        vec4 originColor = texture(_MainTex, xlv_TEXCOORD0);
        originColor = vec4(originColor.rgb * INTENSITY, originColor.a);
        vec4 bloomColor = texture(_BlurTex, xlv_TEXCOORD0);
        color = originColor + bloomColor;
    }

    // vec4 c = texture(_MainTex, xlv_TEXCOORD0);
    // vec4 cur_color;
    // cur_color = fastBlur(_MainTex, xlv_TEXCOORD0,BLUR_SPREAD);
    //
    // c = vec4(c.rgb * INTENSITY, c.a) / 2.0;
    // color = c;
}

blur.fs.glsl:
#version 300 es

precision mediump float;

uniform sampler2D _MainTex;
uniform lowp float _BlurGap; //卷积每层间隔单位
uniform highp vec4 _MainTex_TexelSize;
in highp vec2 xlv_TEXCOORD0;
//texture2DEtC1Mark

out vec4 color; 
void main() 
{
	lowp float offset_x = _MainTex_TexelSize.x * _BlurGap;
	lowp float offset_y = _MainTex_TexelSize.y * _BlurGap;
    highp vec4 sample0,sample1,sample2,sample3;
	sample0=texture(_MainTex,vec2(xlv_TEXCOORD0.x-offset_x,xlv_TEXCOORD0.y-offset_y));
	sample1=texture(_MainTex,vec2(xlv_TEXCOORD0.x+offset_x,xlv_TEXCOORD0.y-offset_y));
	sample2=texture(_MainTex,vec2(xlv_TEXCOORD0.x+offset_x,xlv_TEXCOORD0.y+offset_y));
	sample3=texture(_MainTex,vec2(xlv_TEXCOORD0.x-offset_x,xlv_TEXCOORD0.y+offset_y));
	highp vec4 _color=(sample0+sample1+sample2+sample3) / 4.0;
    color = _color;
}

contort.fs.glsl:
#version 300 es

#ifdef GL_FRAGMENT_PRECISION_HIGH
precision highp float;
#else
precision mediump float;
#endif

const float PI = 3.14159265;
uniform sampler2D _MainTex;
uniform float _UD;
uniform float _UR;
in highp vec2 xlv_TEXCOORD0;
//texture2DEtC1Mark

out vec4 color; 
void main()
{
    ivec2 ires = ivec2(1024, 1024);
    float Res = float(ires.s);

    vec2 st = xlv_TEXCOORD0;
    float Radius = Res * _UR;
    vec2 xy = Res * st;

    vec2 dxy = xy - vec2(Res / 2.0, Res / 2.0);
    float r = length(dxy);
    float beta = atan(dxy.y, dxy.x) + radians(_UD) * 2.0 * (Radius - r) / Radius;

    vec2 xy1 = xy;
    if (r <= Radius)
    {
        xy1 = Res / 2.0 + r * vec2(cos(beta), sin(beta));
    }
    st = xy1 / Res;

    vec3 irgb = texture(_MainTex, st).rgb;

    color = vec4(irgb, 1.0);
}

