uniform samplerCube u_sky_3;// IBL
uniform samplerCube u_sky_4;// IBL
uniform lowp vec4 _MainColor;
uniform vec4 glstate_eyepos;

in vec3 v_pos;
in mediump vec2 xlv_TEXCOORD0;
in highp vec4 v_color;

in vec3 v_normal;
in mat3 TBN;

